import React from 'react';
import './App.css';
import './components/PupsBar'
import PupsBar from './components/PupsBar';
import Pup from './components/Pup'

class App extends React.Component {
  state= {
    renderPup:false,
    pups:[]
  }

  componentDidMount(){
    fetch('http://localhost:3000/pups').then(res=>res.json()).then(pupList=>this.setState({pups:pupList}))
  }

  findPup=(pupId)=>{
    const pup = this.state.pups.find(pup => pup.id === pupId)
    this.setState({renderPup: pup})
  }

  toggleGoodDog = (pupId) => {
    const pup = this.state.pups.find(pup => pup.id === pupId)
    pup.isGoodDog = !pup.isGoodDog

    let reqObj = {
      headers: { "Content-Type": "application/json" },
      method: "PATCH",
      body: JSON.stringify(pup),
    };

    fetch('http://localhost:3000/pups/' + pup.id, reqObj)
      .then((res) => res.json())
      .then(updatedPup => {
        const index = this.state.pups.findIndex((existingPup) => existingPup.id === updatedPup.id);
        // if (index === -1) {
        //   return;
        // }
        this.setState(previousState => {
          previousState.pups[index] = updatedPup
          return { pups: previousState}});
      });
  }




  render(){
  return (
      <div className="App">
        <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div>
        <div id="dog-bar">
          {this.state.pups.map(pup => <PupsBar pup={pup} key={pup.id} showPup={this.findPup}/>)}
        </div>
        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">
            {this.state.renderPup? <Pup pup={this.state.renderPup} toggleGoodDog={this.toggleGoodDog} />:null}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
